// src/components/Home.js
import React from 'react';
import '../styles/Home.css';

const panels = [
  {
    title: "Secure and Enterprise-ready",
    icon: "ðŸ”’", // Placeholder for icon; replace with an actual icon if needed
    description: "Meet security and operational requirements to bring AI products to market faster.",
    details: [
      { subtitle: "Secure", text: "Control your data and know itâ€™s safe. Our services are SOC 2 and HIPAA certified." },
      { subtitle: "Reliable", text: "Powering mission-critical applications with SLA and observability support." },
      { subtitle: "Cloud-native", text: "Fully managed in the cloud of your choice, with support for AWS, Azure, GCP." },
    ],
  },
  {
    title: "Optimized Client Relationships",
    icon: "ðŸ¤–",
    description: "The most comprehensive set of agents dedicated to optimizing client relationships.",
    details: [
      { subtitle: "Semantic Search", text: "Find clients interested in specific topics like bonds or tax proposals." },
      { subtitle: "Client Insight", text: "Get insights on clientsâ€™ readiness for meetings and sales." },
      { subtitle: "Meeting Briefings", text: "Summarize action items, concerns, and updates in one place." },
    ],
  },
  {
    title: "Our Work",
    icon: "ðŸ’¼",
    description: "Pioneering the future of AI with innovative projects and groundbreaking research.",
    details: [
      { subtitle: "Claude for Enterprise", date: "Sep 4, 2024", type: "Product" },
      { subtitle: "Constitutional AI: Harmlessness from AI Feedback", date: "Dec 15, 2022", type: "Research" },
      { subtitle: "Core Views on AI Safety", date: "Mar 8, 2023", type: "Announcements" },
    ],
  },
];

const Home = () => (
  <div className="home-container">
    <h1 className="home-title">Welcome to Autonomy Labs</h1>
    <p className="home-description">
      At Autonomy Labs, we are creating autonomous, self-assembling AI models for a new era of finance and industry automation.
    </p>

    <div className="panel-sections">
      {panels.map((panel, index) => (
        <section key={index} className="panel-section">
          <h2 className="panel-title">{panel.icon} {panel.title}</h2>
          <p className="panel-description">{panel.description}</p>
          <div className="panel-details">
            {panel.details.map((detail, idx) => (
              <div key={idx} className="panel-detail">
                <h3 className="panel-subtitle">{detail.subtitle}</h3>
                <p className="panel-text">
                  {detail.text || `${detail.date} - ${detail.type}`}
                </p>
              </div>
            ))}
          </div>
        </section>
      ))}
    </div>
  </div>
);

export default Home;
