// src/components/InfoTabsSection.js
import React, { useState } from 'react';
import '../styles/InfoTabsSection.css';

const InfoTabsSection = () => {
  const [activeTab, setActiveTab] = useState("World Models");

  const tabs = [
    { title: "World Models", content: "Learn and continuously update..." },
    { title: "Probabilistic Reasoning", content: "Agents that work reliably..." },
    { title: "Augment", content: "Digital robots observe your workflows..." },
  ];

  const handleTabClick = (title) => {
    setActiveTab(title);
  };

  return (
    <div className="info-tabs-section">
      <h2 className="vision-title">Vision</h2>
      <div className="tabs-container">
        <div className="tab-buttons">
          {tabs.map((tab) => (
            <button
              key={tab.title}
              className={`tab-button ${activeTab === tab.title ? "active" : ""}`}
              onClick={() => handleTabClick(tab.title)}
            >
              {tab.title}
            </button>
          ))}
        </div>
        <div className="tab-content">
          {tabs.map(
            (tab) =>
              activeTab === tab.title && (
                <div key={tab.title}>
                  <h4>{tab.title}</h4>
                  <p>{tab.content}</p>
                </div>
              )
          )}
        </div>
      </div>
    </div>
  );
};

export default InfoTabsSection;
